import React from 'react';
import { getAllProducts } from '../data/products';
import { Bag } from './Bag';


export const Product = (props) => {
    const { product,setAmountBag } = props;
    return <div><li>name: {getAllProducts()[product].name} | price: {getAllProducts()[product].price}  </li>
    <button onClick={()=>setAmountBag(<Bag product={product} empty={false}/>)}>add to bag</button></div>;
}
