import React, { useState } from 'react';
import { codes } from '../data/products';
import { Bag } from './Bag';
import { Product } from './Product';

export const Content = () => {
   
    return <div>
        {codes.map(p => <div key={p}><Product product={p} /></div>)}
        <p>amount of products in bag is:{amountBag}</p>
        <button onClick={() => setAmountBag(<Bag product={undefined} empty={true} />)}>for empty bag</button>
    </div>
}

export  const [amountBag, setAmountBag] = useState(0);
