import {Bag} from './components/Bag';
import { click } from '@testing-library/user-event/dist/click';
function App() {
  let amountProduct=0;
  //const products = getAllProducts();
  return (
    <div className="App">
      {codes.map(p => <div><Product product={p} /><button onClick={()=>click(amountProduct=Bag(p))}>add to bag</button></div>)
   }
        <p>amount of products in bag is:{amountProduct}</p>
    </div>
  );
}

export default App;
