import React from 'react';
import {getData} from'../data/workers';



export const Worker = (props) => {
    const { w } = useParams();
    const worker = getData(parseInt(w));
    useEffect(() => {
        if (!worker) {
            console.log('no worker');
            navigate('/WorkersList');
        }
    }, [w]);
    return worker ?<div >
        name: {worker.name}
        phone: {worker.phone}
        address: {worker.address}
    </div>: 'no such a worker';
}
