import React, { useState, useRef } from 'react';
import {  useParams } from 'react-router-dom';
import { useRecoilState } from 'recoil';
import { tasksState } from '../data/atoms';
import { getTask } from '../data/tasks';
import { useNavigate} from 'react-router-dom';

export const EditTask = () => {
    const [tasks,setTasks] = useRecoilState(tasksState);
    const formEl = useRef();
    const {id}=useParams();
    const task=tasks.find(t=>t.id===parseInt(id));
 
    const editTask = (e) => {
        e.preventDefault();
        const newTask = {
            id:task.id,
            description: formEl.current.description.value,
            done:formEl.current.done.checked,
        };
        console.log(newTask);
        const index=tasks.findIndex(t=>t.id===newTask.id);
        let tasks2=[...tasks];
        tasks2[index]=newTask;
        setTasks(tasks2);
        formEl.current.reset();
        const navigate = useNavigate();
        navigate("");
    }
    return <form onSubmit={editTask} ref={formEl} >
        <h3>edit Task</h3>
        <input type="text"  defaultValue={task.description} name="description" ></input> <br/>
        <input type="checkbox" defaultChecked={task.done} name="done"/>
        <button type="submit">save</button>
    </form>
}
