import React, { useState } from 'react';
import { codes } from '../data/products';
import { Bag } from './Bag';
import { Product } from './Product';


export const Content = () => {
    const [amountBag, setAmountBag] = useState(0);

    const changeAmount=(p,empty)=>{
        <Bag product={p} empty={empty}></Bag>
        setTimeout(() => {
            setAmountBag(<Bag product={undefined} empty={false}></Bag>)
        }, 3000);
      
    }
    return <div>
        {codes.map(p => <div key={p}><Product product={p} setamount={changeAmount}/></div>)}
        <p>amount of products in bag is:{amountBag}</p>
        <button onClick={() => changeAmount(undefined,true)}>for empty bag</button>
    </div>
}
