import './App.css';
import { Product } from './components/Product'
//import { getAllProducts } from './data/products';
import { codes } from './data/products';
import {Bag} from './components/Bag';
import { click } from '@testing-library/user-event/dist/click';
import React,{ useState } from 'react';

function App() {
  const [amountBag,setAmountBag]=useState(0);
  const changeAmount=(amount)=>{
    {setAmountBag(amount)};
  }
  return (
    <div className="App">
      {codes.map(p => <div><Product product={p} /><button onClick={()=>click(changeAmount(<Bag product={p} empty={false}/>))}>add to bag</button></div>)
   }

        <p>amount of products in bag is:{amountBag}</p>
        <button onClick={()=>click(changeAmount(<Bag product={undefined} empty={true}/>))}>for empty bag</button>
    </div>
  );
}

export default App;
