import logo from './logo.svg';
import './App.css';
import { Product } from './components/Product'
import { getAllProducts } from './data/products';
import { codes } from './data/products';
import {Bag} from './Bag';
function App() {
  const products = getAllProducts();
  return (
    <div className="App">
      {codes.map(p => <button onClick={Bag(p)}>add to bag</button>,
      <Product product={p} />)
   }

    </div>
  );
}

export default App;
