//import logo from './logo.svg';
import './App.css';
import { Product } from './components/Product'
//import { getAllProducts } from './data/products';
import { codes } from './data/products';
import {Bag} from './components/Bag';
import { click } from '@testing-library/user-event/dist/click';
import React,{ useState } from 'react';
import { getAllProducts } from './data/products';
function App() {
  const [amountBag,setAmountBag]=useState(0);
  const changeAmount=()=>{
    {setAmountBag(Bag())};
  }
  //const products = getAllProducts();
  return (
    <div className="App">
      {codes.map(p => <div><Product product={getAllProducts()[p]} /><button onClick={()=>click(Bag(product),changeAmount())}>add to bag</button></div>)
   }

        <p>amount of products in bag is:{amountBag}</p>
        <button onClick={()=>click(Bag(false),changeAmount())}>for empty bag</button>
    </div>
  );
}

export default App;
