import {Bag} from './components/Bag';
function App() {
  //const products = getAllProducts();
  return (
    <div className="App">
      {codes.map(p => <div><Product product={p} /><button onClick={Bag(p)}>add to bag</button></div>)
   }

    </div>
  );
}

export default App;
