import { getAllProducts } from '../data/products';
const bagList=[];
export const Bag=(props)=>{
   // const {product}=props;
   const {product, clear}=props;

   if(product!=undefined&&clear===false)
        bagList.push(getAllProducts()[product]);
     if(clear===true)
        bagList=[];
    console.log(bagList.length);
    return bagList.length;
}
