import React from 'react';

export const ProductsInBag=(props)=>{
    const {bag,moveProduct}=props;
    return<div>
        {bag.map(p=><div>
            name:{p.name} | quantity:{p.quantity} | price for unit:{p.price} | total price:{p.price*p.quantity}
            <button onClick={()=>moveProduct(p)}>move from the bag</button>
        </div>)}

    </div>
}
