import { atom, selector } from 'recoil';
import { tasks } from './tasks';


export const tasksState = atom({
    key: 'tasksState',
    default: tasks,
});


export const tasksNum = selector({
    key: 'tasksNum',
    get: ({ get }) => {
      
        const tasks = get(tasksState);
        const count = tasks.filter(t=>t.done===true).length;
        return count;
    }
})

export const allTasksNum = selector({
    key: 'allTasksNum',
    get: ({ get }) => {
      
        const tasks = get(tasksState);
        const count = tasks.length;
        return count;
    }
})
