import {Bag} from './components/Bag';
import { click } from '@testing-library/user-event/dist/click';
import React,{ useState } from 'react';
const [amountBag,setAmountBag]=useState(0);
function App() {

  //const products = getAllProducts();
  return (
    <div className="App">
      {codes.map(p => <div><Product product={p} /><button onClick={()=>click(Bag(p),changeAmount())}>add to bag</button></div>)
   }

        <p>amount of products in bag is:{amountBag}</p>
    </div>
  );
}
const changeAmount=()=>{
  {setAmountBag(Bag())};
}
export default App;
