import React, { useState } from 'react';
import { getAllProducts } from '../data/products';
import {Bag} from './Bag';

export const Product = (props) => {
    const { product } = props;
    return <li>name: {getAllProducts()[product].name} | price: {getAllProducts()[product].price} <button onClick={Bag(product)}>add to bag</button> </li>;
}
