import React, { useState } from 'react';
import { codes, getData } from '../data/products';
import { Product } from './Product';


export const Content = () => {
    const [amountBag, setAmountBag] = useState(0);
    const [bag,setBag]=useState([]);
    const addToBag=(p)=>{
        const bag2=bag;
        if(bag2[p])
        {
            bag2[p].quantity++;
        }
        else
            bag2.push(getData[product]);
        setAmountBag(bag2.length);
        setBag(bag2);
    }
    const clear=()=>{
        setAmountBag(0);
        setBag([]);
    }
    return <div>
        {codes.map(p => <div key={p}><Product product={p} setToBag={addToBag}/></div>)}
        <p>amount of products in bag is:{amountBag}</p>
        <button onClick={() => clear()}>for empty bag</button>
    </div>
}
