import React, { useState, useRef } from 'react';
import { useParams } from 'react-router-dom';
import { useRecoilState } from 'recoil';
import { tasksState } from '../data/atoms';
import { getTask } from '../data/tasks';

export const EditTask = () => {
    const [tasks,setTasks] = useRecoilState(tasksState);
    const formEl = useRef();

    const editTask = (e) => {
        e.preventDefault();
        const newTask = {
            id:task.id,
            description: formEl.current.description.value,
            done:formEl.current.done.value,
        };
        console.log(newTask);
        let tasks2=[...tasks];
        const index=tasks2.findIndex(task.id);
        tasks2[index]=newTask;
        setTasks(tasks2);
        formEl.current.reset();
    }
    
    const {id}=parseInt(useParams());
    const task=getTask(id);
    return <form onSubmit={editTask} ref={formEl}>
        <h3>edit Task</h3>
        <input type="text" value={task.description} name="description" /> <br/>
        <input type="checkbox" value={task.done} name="done"/>
        <button type="submit">edit Task</button>
    </form>
}
